      rating: 4.9,
      reviewCount: 127,
      image: 'https://i.pinimg.com/1200x/ad/62/12/ad6212628fa7ca2851db2f90bef2bf58.jpg',
      badge: 'BEST',
      category: 'ÏÉùÏùºÏºÄÏù¥ÌÅ¨'
    },
    {
      id: 2,
      name: 'Ï¥àÏΩúÎ¶ø Î¨¥Ïä§ ÏºÄÏù¥ÌÅ¨',
      description: 'ÏßÑÌïú Ï¥àÏΩúÎ¶øÍ≥º Î∂ÄÎìúÎü¨Ïö¥ Î¨¥Ïä§',
      price: 45000,
      rating: 4.7,
      reviewCount: 89,
      image: 'https://i.pinimg.com/736x/65/b5/19/65b519ed94b29e7e92c22fe7c6e3ecef.jpg',
      badge: 'NEW',
      category: 'ÏÉùÏùºÏºÄÏù¥ÌÅ¨'
    },
    {
      id: 3,
      name: 'Îâ¥Ïöï ÏπòÏ¶àÏºÄÏù¥ÌÅ¨',
      description: 'ÏßÑÏßú Îâ¥ÏöïÏùò Îßõ, ÏßÑÌïú ÏπòÏ¶à',
      price: 28000,
      rating: 4.3,
      reviewCount: 156,
      image: 'https://i.pinimg.com/474x/12/45/00/124500c8574e4b3ac1655fe44758a139.jpg',
      category: 'ÎîîÏ†ÄÌä∏'
    },
    {
      id: 4,
      name: 'Ïù¥ÌÉàÎ¶¨Ïïà Ìã∞ÎùºÎØ∏Ïàò',
      description: 'Ï†ïÌÜµ Ïù¥ÌÉàÎ¶¨Ïïà Î†àÏãúÌîº',
      price: 38000,
      rating: 4.9,
      reviewCount: 203,
      image: 'https://i.pinimg.com/1200x/53/2b/f1/532bf12e533bb1a9c04cfa84dc4bf510.jpg',
      badge: 'ÏãúÏ¶åÌïúÏ†ï',
      category: 'ÎîîÏ†ÄÌä∏'
    }
  ];

  const filteredProducts = products.filter(product => {
    const matchesFilter = activeFilter === 'Ï†ÑÏ≤¥' || product.category === activeFilter;
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  const formatPrice = (price) => {
    return new Intl.NumberFormat('ko-KR').format(price);
  };

  const handleAddToCart = (product) => {
    addToCart(product);
    showSuccess(`${product.name}Ïù¥(Í∞Ä) Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§! ‚ú®`, 2500);
  };


  return (
    <div className="App">
      <Background />
      <ToastContainer toasts={toasts} removeToast={removeToast} />

      {/* Ïû•Î∞îÍµ¨Îãà Î≤ÑÌäº (Ïò§Î•∏Ï™Ω ÌïòÎã® Í≥†Ï†ï) */}
      <div style={{ position: 'fixed', bottom: '24px', right: '24px', zIndex: 1000 }}>
        <Link
          to="/cart"
          className="cart-fab"
          title="Ïû•Î∞îÍµ¨Îãà"
        >
