import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import Background from './Background';

const CartPage = () => {
  const {
    items,
    removeFromCart,
    updateQuantity,
    clearCart,
    resetToSampleData,
    getTotalItems,
    getTotalPrice,
    getDiscountedTotal,
    getTotalDiscount
  } = useCart();

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(productId);
    } else {
      updateQuantity(productId, newQuantity);
    }
  };

  const handleCheckout = () => {
    if (items.length === 0) {
      alert('Ïû•Î∞îÍµ¨ÎãàÍ∞Ä ÎπÑÏñ¥ÏûàÏäµÎãàÎã§.');
      return;
    }

    // Ïó¨Í∏∞Ïóê Í≤∞Ï†ú Î°úÏßÅ Ï∂îÍ∞Ä
    alert(`Ï¥ù ${getTotalItems()}Í∞úÏùò ÏÉÅÌíàÏùÑ Ï£ºÎ¨∏ÌïòÏãúÍ≤†ÏäµÎãàÍπå?\nÏ¥ù Í∏àÏï°: ${getTotalPrice().toLocaleString()}Ïõê`);
  };

  const formatPrice = (price) => {
    return price.toLocaleString();
  };

  return (
    <div className="App">
      <Background />

      {/* Ïû•Î∞îÍµ¨Îãà Î≤ÑÌäº (Í≥†Ï†ï ÏúÑÏπò) */}
      <div className="fixed top-4 right-4 z-50">
        <Link
          to="/"
          className="relative bg-gray-600 hover:bg-gray-700 text-white p-3 rounded-full shadow-lg transition-all duration-300 hover:scale-110 group"
          title="ÌôàÏúºÎ°ú"
        >
          <span className="text-xl">üè†</span>
          <div className="absolute right-full top-1/2 transform -translate-y-1/2 mr-2 bg-gray-800 text-white px-2 py-1 rounded text-sm opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
            ÌôàÏúºÎ°ú
          </div>
        </Link>
      </div>

      {/* Ìó§Îçî */}
      <div className="relative z-10 min-h-screen">
        <div className="container mx-auto px-4 py-8">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-4xl font-bold text-white mb-2">üõí Ïû•Î∞îÍµ¨Îãà</h1>
              <p className="text-gray-300">ÏÑ†ÌÉùÌïú ÏÉÅÌíàÎì§ÏùÑ ÌôïÏù∏ÌïòÍ≥† Ï£ºÎ¨∏ÌïòÏÑ∏Ïöî</p>
            </div>
            <div className="text-right">
              <div className="text-white text-lg">
                Ï¥ù <span className="font-bold text-yellow-400">{getTotalItems()}</span>Í∞ú ÏÉÅÌíà
              </div>
              {getTotalDiscount() > 0 && (
                <div className="text-sm text-green-400">
                  Ìï†Ïù∏ Í∏àÏï°: {formatPrice(getTotalDiscount())}Ïõê
                </div>
              )}
            </div>
          </div>

          {/* Ïû•Î∞îÍµ¨Îãà ÎÇ¥Ïö© */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* ÏÉÅÌíà Î™©Î°ù */}
            <div className="lg:col-span-2">
              {items.length === 0 ? (
                <div className="glass-card p-8 text-center">
                  <div className="text-6xl mb-4">üõí</div>
                  <h3 className="text-xl font-semibold text-white mb-2">Ïû•Î∞îÍµ¨ÎãàÍ∞Ä ÎπÑÏñ¥ÏûàÏäµÎãàÎã§</h3>
                  <p className="text-gray-300 mb-6">ÎßõÏûàÎäî ÏºÄÏù¥ÌÅ¨Î•º Îã¥ÏïÑÎ≥¥ÏÑ∏Ïöî!</p>
                  <a 
                    href="/cakes" 
                    className="inline-block bg-pink-600 hover:bg-pink-700 text-white px-6 py-3 rounded-lg transition-colors"
                  >
                    ÏºÄÏù¥ÌÅ¨ Î≥¥Îü¨Í∞ÄÍ∏∞
                  </a>
                </div>
              ) : (
                <div className="space-y-4">
                  {items.map((item) => (
                    <div key={item.id} className="glass-card p-6">
                      <div className="flex items-center space-x-4">
                        {/* ÏÉÅÌíà Ïù¥ÎØ∏ÏßÄ */}
                        <div className="flex-shrink-0">
                          <img
                            src={item.image || '/api/placeholder/150/150'}
                            alt={item.name}
                            className="w-20 h-20 object-cover rounded-lg"
                          />
                        </div>

                        {/* ÏÉÅÌíà Ï†ïÎ≥¥ */}
                        <div className="flex-grow">
                          <h3 className="text-lg font-semibold text-white mb-1">
                            {item.name}
                          </h3>
                          <p className="text-gray-300 text-sm mb-2">{item.category}</p>
                          <div className="flex items-center space-x-4">
                            {item.originalPrice && item.originalPrice > item.price && (
                              <span className="text-gray-400 line-through text-sm">
                                {formatPrice(item.originalPrice)}Ïõê
                              </span>
                            )}
                            <span className="text-pink-400 font-semibold">
                              {formatPrice(item.price)}Ïõê
                            </span>
                          </div>
                        </div>

                        {/* ÏàòÎüâ Ï°∞Ï†à */}
                        <div className="flex items-center space-x-3">
                          <button
                            onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                            className="w-8 h-8 bg-gray-600 hover:bg-gray-500 text-white rounded-full flex items-center justify-center transition-colors"
                          >
                            -
                          </button>
                          <span className="text-white font-semibold min-w-[2rem] text-center">
                            {item.quantity}
                          </span>
                          <button
                            onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                            className="w-8 h-8 bg-gray-600 hover:bg-gray-500 text-white rounded-full flex items-center justify-center transition-colors"
                          >
                            +
                          </button>
                        </div>

                        {/* ÏÇ≠Ï†ú Î≤ÑÌäº */}
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="text-red-400 hover:text-red-300 transition-colors p-2"
                          title="ÏÇ≠Ï†ú"
                        >
                          üóëÔ∏è
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
