import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import Background from './Background';
import { productAPI } from '../utils/api';

const CakeDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, getTotalItems } = useCart();
  const [cake, setCake] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);

  // ÏòàÏãú ÏºÄÏù¥ÌÅ¨ Îç∞Ïù¥ÌÑ∞ (Î∞±ÏóîÎìú Ïó∞Í≤∞ Ïã§Ìå® Ïãú ÏÇ¨Ïö©)
  const sampleCakes = {
    1: {
      id: 1,
      name: 'Îî∏Í∏∞ ÏÉùÌÅ¨Î¶º ÏºÄÏù¥ÌÅ¨',
      description: 'Ïã†ÏÑ†Ìïú Îî∏Í∏∞ÏôÄ Î∂ÄÎìúÎü¨Ïö¥ ÏÉùÌÅ¨Î¶ºÏúºÎ°ú ÎßåÎì† ÌÅ¥ÎûòÏãùÌïú ÏºÄÏù¥ÌÅ¨ÏûÖÎãàÎã§. Îã¨ÏΩ§ÌïòÍ≥† ÏÉÅÌÅºÌïú Îî∏Í∏∞Í∞Ä ÏÉùÌÅ¨Î¶ºÍ≥º ÏôÑÎ≤ΩÌïòÍ≤å Ïñ¥Ïö∞Îü¨Ï†∏ ÏûÖÏïàÏóêÏÑú ÏÇ¨Î•¥Î•¥ ÎÖπÎäî ÎßõÏùÑ ÏÑ†ÏÇ¨Ìï©ÎãàÎã§.',
      price: 35000,
      originalPrice: 42000,
      rating: 4.9,
      reviewCount: 127,
      images: [
        'https://i.pinimg.com/1200x/ad/62/12/ad6212628fa7ca2851db2f90bef2bf58.jpg',
        'https://i.pinimg.com/1200x/8c/92/84/8c9284e8c4e5b5e5e5e5e5e5e5e5e5e5.jpg',
        'https://i.pinimg.com/1200x/5a/7b/9c/5a7b9ce5e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg'
      ],
      badge: 'BEST',
      category: 'ÏÉùÏùºÏºÄÏù¥ÌÅ¨',
      ingredients: ['Îî∏Í∏∞', 'ÏÉùÌÅ¨Î¶º', 'Î∞îÎãêÎùº Ïä§ÌéÄÏßÄ', 'ÏÑ§ÌÉï', 'Î≤ÑÌÑ∞'],
      size: '6Ïù∏Î∂Ñ',
      shelfLife: '2Ïùº',
      allergens: ['Ïö∞Ïú†', 'Îã¨Í±Ä', 'Î∞ÄÍ∞ÄÎ£®'],
      nutrition: {
        calories: 320,
        protein: 6,
        fat: 18,
        carbs: 35
      }
    },
    2: {
      id: 2,
      name: 'Ï¥àÏΩúÎ¶ø Î¨¥Ïä§ ÏºÄÏù¥ÌÅ¨',
      description: 'ÏßÑÌïú Î≤®Í∏∞Ïóê Ï¥àÏΩúÎ¶øÏúºÎ°ú ÎßåÎì† Î∂ÄÎìúÎü¨Ïö¥ Î¨¥Ïä§ ÏºÄÏù¥ÌÅ¨ÏûÖÎãàÎã§. Ï¥àÏΩúÎ¶øÏùò ÏßÑÌïú ÎßõÍ≥º Î∂ÄÎìúÎü¨Ïö¥ ÏßàÍ∞êÏù¥ Ï°∞ÌôîÎ•º Ïù¥Î§Ñ Ï¥àÏΩúÎ¶ø Ïï†Ìò∏Í∞ÄÎì§ÏóêÍ≤å ÏµúÍ≥†Ïùò ÏÑ†ÌÉùÏûÖÎãàÎã§.',
      price: 38000,
      originalPrice: 45000,
      rating: 4.8,
      reviewCount: 89,
      images: [
        'https://i.pinimg.com/1200x/8c/92/84/8c9284e8c4e5b5e5e5e5e5e5e5e5e5e5.jpg',
        'https://i.pinimg.com/1200x/ad/62/12/ad6212628fa7ca2851db2f90bef2bf58.jpg'
      ],
      badge: 'HOT',
      category: 'ÏÉùÏùºÏºÄÏù¥ÌÅ¨',
      ingredients: ['Î≤®Í∏∞Ïóê Ï¥àÏΩúÎ¶ø', 'ÏÉùÌÅ¨Î¶º', 'Îã¨Í±Ä', 'Î≤ÑÌÑ∞', 'ÏÑ§ÌÉï'],
      size: '6Ïù∏Î∂Ñ',
      shelfLife: '3Ïùº',
      allergens: ['Ïö∞Ïú†', 'Îã¨Í±Ä', 'Î∞ÄÍ∞ÄÎ£®', 'Í≤¨Í≥ºÎ•ò'],
      nutrition: {
        calories: 380,
        protein: 8,
        fat: 22,
        carbs: 38
      }
    },
    3: {
      id: 3,
      name: 'Ìã∞ÎùºÎØ∏Ïàò',
      description: 'Ïù¥ÌÉàÎ¶¨ÏïÑ Ï†ÑÌÜµ ÎîîÏ†ÄÌä∏ Ìã∞ÎùºÎØ∏ÏàòÏûÖÎãàÎã§. ÎßàÏä§Ïπ¥Ìè¨ÎÑ§ ÏπòÏ¶àÏôÄ ÏóêÏä§ÌîÑÎ†àÏÜåÍ∞Ä ÎßåÎÇò ÎßåÎìúÎäî Ï†àÎ¨òÌïú ÎßõÏùò ÎîîÏ†ÄÌä∏Î°ú, Ïª§ÌîºÏôÄ ÏπòÏ¶àÏùò Ï°∞ÌôîÍ∞Ä ÏùºÌíàÏûÖÎãàÎã§.',
      price: 28000,
      originalPrice: null,
      rating: 4.7,
      reviewCount: 156,
      images: [
        'https://i.pinimg.com/1200x/7b/3a/45/7b3a45e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg',
        'https://i.pinimg.com/1200x/5a/7b/9c/5a7b9ce5e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg'
      ],
      badge: 'NEW',
      category: 'ÎîîÏ†ÄÌä∏',
      ingredients: ['ÎßàÏä§Ïπ¥Ìè¨ÎÑ§ ÏπòÏ¶à', 'ÏóêÏä§ÌîÑÎ†àÏÜå', 'Î†àÎîî ÌîºÍ±∞', 'ÏΩîÏΩîÏïÑ ÌååÏö∞Îçî', 'ÏÑ§ÌÉï'],
      size: '4Ïù∏Î∂Ñ',
      shelfLife: '2Ïùº',
      allergens: ['Ïö∞Ïú†', 'Îã¨Í±Ä', 'Î∞ÄÍ∞ÄÎ£®', 'Ïπ¥ÌéòÏù∏'],
      nutrition: {
        calories: 280,
        protein: 7,
        fat: 16,
        carbs: 28
      }
    },
    4: {
      id: 4,
      name: 'Ïõ®Îî© ÏºÄÏù¥ÌÅ¨',
      description: 'ÌäπÎ≥ÑÌïú ÎÇ†ÏùÑ ÏúÑÌïú Îü≠ÏÖîÎ¶¨ Ïõ®Îî© ÏºÄÏù¥ÌÅ¨ÏûÖÎãàÎã§. Ïö∞ÏïÑÌïú ÎîîÏûêÏù∏Í≥º Í≥†Í∏âÏä§Îü¨Ïö¥ Ïû¨Î£åÎ°ú ÎßåÎì§Ïñ¥Ï†∏ Ïã†ÎûëÏã†Î∂ÄÏùò ÏÜåÏ§ëÌïú ÏàúÍ∞ÑÏùÑ ÎçîÏö± ÌäπÎ≥ÑÌïòÍ≤å ÎßåÎì§Ïñ¥ÎìúÎ¶ΩÎãàÎã§.',
      price: 120000,
      originalPrice: 150000,
      rating: 4.9,
      reviewCount: 45,
      images: [
        'https://i.pinimg.com/1200x/9c/4a/67/9c4a67e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg',
        'https://i.pinimg.com/1200x/ad/62/12/ad6212628fa7ca2851db2f90bef2bf58.jpg'
      ],
      badge: 'PREMIUM',
      category: 'Ïõ®Îî©ÏºÄÏù¥ÌÅ¨',
      ingredients: ['ÌîÑÎ¶¨ÎØ∏ÏóÑ Î∞îÎãêÎùº', 'ÎßàÏä§Ïπ¥Ìè¨ÎÑ§', 'Ïã†ÏÑ†Ìïú Í≥ºÏùº', 'Í≥®Îìú Î¶¨ÌîÑ', 'ÏÑ§ÌÉï'],
      size: '20Ïù∏Î∂Ñ',
      shelfLife: '3Ïùº',
      allergens: ['Ïö∞Ïú†', 'Îã¨Í±Ä', 'Î∞ÄÍ∞ÄÎ£®'],
      nutrition: {
        calories: 350,
        protein: 6,
        fat: 20,
        carbs: 40
      }
    }
  };

  useEffect(() => {
    const fetchCake = async () => {
      try {
        setLoading(true);
        // Î∞±ÏóîÎìúÏóêÏÑú ÏÉÅÌíà ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const data = await productAPI.getProduct(id);
        setCake(data);
      } catch (error) {
        console.error('ÏÉÅÌíà ÏÉÅÏÑ∏ Ï°∞Ìöå Ïã§Ìå®:', error);
        // ÏóêÎü¨ Ïãú ÏòàÏãú Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
        const sampleCake = sampleCakes[id];
        if (sampleCake) {
          setCake(sampleCake);
        } else {
          alert('ÏÉÅÌíàÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
          navigate('/cakes');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchCake();
  }, [id, navigate]);

  const handleAddToCart = () => {
    if (cake) {
      const cartItem = {
        ...cake,
        image: cake.images[0], // Ï≤´ Î≤àÏß∏ Ïù¥ÎØ∏ÏßÄÎ•º ÎåÄÌëú Ïù¥ÎØ∏ÏßÄÎ°ú ÏÇ¨Ïö©
        quantity: quantity
      };
      addToCart(cartItem);
      alert(`${cake.name} ${quantity}Í∞úÍ∞Ä Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!`);
    }
  };

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1 && newQuantity <= 10) {
      setQuantity(newQuantity);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('ko-KR').format(price);
  };

  if (loading) {
    return (
      <div className="App">
        <Background />
        <div className="relative z-10 min-h-screen flex items-center justify-center">
          <div className="text-white text-xl">Î°úÎî© Ï§ë...</div>
        </div>
      </div>
    );
  }

  if (!cake) {
    return (
      <div className="App">
        <Background />
        <div className="relative z-10 min-h-screen flex items-center justify-center">
          <div className="text-white text-xl">ÏÉÅÌíàÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <Background />

      {/* Ïû•Î∞îÍµ¨Îãà Î≤ÑÌäº (Í≥†Ï†ï ÏúÑÏπò) */}
      <div className="fixed top-4 right-4 z-50">
        <Link
          to="/cart"
          className="relative bg-pink-600 hover:bg-pink-700 text-white p-3 rounded-full shadow-lg transition-all duration-300 hover:scale-110 group"
          title="Ïû•Î∞îÍµ¨Îãà"
        >
          <span className="text-xl">üõí</span>
          {getTotalItems() > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center font-bold animate-pulse">
              {getTotalItems()}
            </span>
          )}
          <div className="absolute right-full top-1/2 transform -translate-y-1/2 mr-2 bg-gray-800 text-white px-2 py-1 rounded text-sm opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
            Ïû•Î∞îÍµ¨Îãà ({getTotalItems()}Í∞ú)
          </div>
        </Link>
      </div>

      <div className="relative z-10 min-h-screen">
        <div className="container mx-auto px-4 py-8">
          {/* Îí§Î°úÍ∞ÄÍ∏∞ Î≤ÑÌäº */}
          <button
            onClick={() => navigate('/cakes')}
            className="mb-6 flex items-center text-gray-300 hover:text-white transition-colors"
          >
            <span className="mr-2">‚Üê</span>
            ÏºÄÏù¥ÌÅ¨ Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            {/* Ïù¥ÎØ∏ÏßÄ ÏÑπÏÖò */}
            <div className="space-y-4">
              {/* Î©îÏù∏ Ïù¥ÎØ∏ÏßÄ */}
              <div className="aspect-square rounded-2xl overflow-hidden bg-gray-800">
                <img
                  src={cake.images[selectedImage]}
                  alt={cake.name}
                  className="w-full h-full object-cover"
                />
              </div>

            </div>

            {/* ÏÉÅÌíà Ï†ïÎ≥¥ ÏÑπÏÖò */}
            <div className="space-y-6">
              {/* Í∏∞Î≥∏ Ï†ïÎ≥¥ */}
              <div>
                <div className="flex items-center gap-3 mb-3">
                  <span className="bg-pink-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                    {cake.badge}
                  </span>
                  <span className="text-gray-400 text-sm">{cake.category}</span>
                </div>

                <h1 className="text-3xl font-bold text-white mb-4">{cake.name}</h1>

                <div className="flex items-center gap-4 mb-4">
                  <div className="flex items-center">
                    <span className="text-yellow-400 mr-1">‚≠ê</span>
                    <span className="text-white font-medium">{cake.rating}</span>
                    <span className="text-gray-400 ml-1">({cake.reviewCount})</span>
                  </div>
                </div>

                <p className="text-gray-300 text-lg leading-relaxed mb-6">
                  {cake.description}
                </p>
              </div>

              {/* Í∞ÄÍ≤© Ï†ïÎ≥¥ */}
              <div className="bg-gray-800/50 rounded-xl p-6">
                <div className="flex items-center mb-4">
                  {cake.originalPrice && cake.originalPrice > cake.price && (
                    <span className="text-gray-400 line-through text-lg mr-8">
                      ‚Ç©{formatPrice(cake.originalPrice)}
                    </span>
                  )}
                  <span className="text-3xl font-bold text-pink-400 mr-8">
                    ‚Ç©{formatPrice(cake.price)}
                  </span>
                  {cake.originalPrice && cake.originalPrice > cake.price && (
                    <span className="bg-green-600 text-white px-4 py-2 rounded-lg font-medium inline-block">
                      {Math.round((1 - cake.price / cake.originalPrice) * 100)}% Ìï†Ïù∏
                    </span>
                  )}
                </div>

                {/* ÏàòÎüâ ÏÑ†ÌÉù */}
                <div className="flex items-center gap-4 mb-6">
                  <span className="text-white font-medium">ÏàòÎüâ:</span>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={() => handleQuantityChange(quantity - 1)}
                      className="w-12 h-12 bg-gray-600 hover:bg-gray-500 text-white rounded-lg flex items-center justify-center transition-colors font-bold text-lg"
                    >
                      ‚àí
                    </button>
                    <button className="w-16 h-12 bg-gray-700 text-white rounded-lg flex items-center justify-center font-bold text-lg">
                      {quantity}
                    </button>
                    <button
                      onClick={() => handleQuantityChange(quantity + 1)}
                      className="w-12 h-12 bg-gray-600 hover:bg-gray-500 text-white rounded-lg flex items-center justify-center transition-colors font-bold text-lg"
                    >
                      +
                    </button>
                  </div>
                </div>

                {/* Ïû•Î∞îÍµ¨Îãà Îã¥Í∏∞ Î≤ÑÌäº */}
                <button
                  onClick={handleAddToCart}
                  className="btn kakao-primary add-to-cart w-full"
                >
                  Ïû•Î∞îÍµ¨ÎãàÏóê Îã¥Í∏∞
                </button>
              </div>

              {/* ÏÉÅÌíà ÏÉÅÏÑ∏ Ï†ïÎ≥¥ */}
              <div className="space-y-6">
