import React, { useState } from 'react';
import { authAPI } from '../utils/api';
import { useAuth } from '../contexts/AuthContext';

const LoginSection = () => {
  const { user, isAuthenticated, loading, logout } = useAuth();
  const [isRequesting, setIsRequesting] = useState(false);
  const [requestStatus, setRequestStatus] = useState(null); // 'success', 'error', null

  // Kakao OAuth Î°úÍ∑∏Ïù∏ ÏãúÏûë
  const handleKakaoLogin = () => {
    authAPI.startKakaoLogin();
  };

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  const handleLogout = async () => {
    if (window.confirm('Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      await logout();
      // AuthContextÏùò logout Ìï®ÏàòÏóêÏÑú Î¶¨Îã§Ïù¥Î†âÌä∏ Ï≤òÎ¶¨
    }
  };

  // ÏÇ¨Ïû•ÎãòÏúºÎ°ú Ï†ÑÌôò ÏöîÏ≤≠
  const handleRequestRoleChange = async () => {
    if (!window.confirm('ÏÇ¨Ïû•ÎãòÏúºÎ°ú Ï†ÑÌôòÏùÑ ÏöîÏ≤≠ÌïòÏãúÍ≤†ÏäµÎãàÍπå?\nÍ¥ÄÎ¶¨ÏûêÏùò ÏäπÏù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.')) {
      return;
    }

    setIsRequesting(true);
    setRequestStatus(null);

    try {
      const response = await authAPI.requestRoleChange();
      if (response.success) {
        setRequestStatus('success');
        alert('ÏÇ¨Ïû•ÎãòÏúºÎ°ú Ï†ÑÌôò ÏöîÏ≤≠Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.\nÍ¥ÄÎ¶¨ÏûêÏùò ÏäπÏù∏ÏùÑ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.');
      } else {
        setRequestStatus('error');
        alert(response.message || 'ÏöîÏ≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('Role change request error:', error);
      setRequestStatus('error');
      const errorMessage = error.response?.data?.message || error.message || 'ÏöîÏ≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.';
      alert(`ÏöîÏ≤≠ Ïã§Ìå®: ${errorMessage}`);
    } finally {
      setIsRequesting(false);
    }
  };

  // Î°úÎî© Ï§ëÏùº Îïå
  if (loading) {
    return (
      <section className="card hero">
        <div className="hero-content">
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            <p>Î°úÎî© Ï§ë...</p>
          </div>
        </div>
      </section>
    );
  }

  // Î°úÍ∑∏Ïù∏Îêú ÏÉÅÌÉú
  if (isAuthenticated && user) {
    return (
      <section className="card hero">
        <div className="hero-content">
          <div className="brand">
            <div className="kakao-logo">
              {user.profile_image ? (
                <img 
                  src={user.profile_image} 
                  alt="ÌîÑÎ°úÌïÑ" 
                  style={{ 
                    width: '64px', 
                    height: '64px', 
                    borderRadius: '50%',
                    objectFit: 'cover'
                  }} 
                />
              ) : (
                <svg width="64" height="64" viewBox="0 0 64 64" fill="none">
