import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Background from './Background';

const FaceCheckinPage = () => {
  const [isScanning, setIsScanning] = useState(true);
  const [scanStatus, setScanStatus] = useState('카메라를 시작하는 중...');
  const [cameraError, setCameraError] = useState(null);
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    // 카메라 스트림 시작
    const startCamera = async () => {
      try {
        setCameraError(null);
        setScanStatus('카메라를 시작하는 중...');

        const stream = await navigator.mediaDevices.getUserMedia({
          video: {
            width: { ideal: 1280 },
            height: { ideal: 720 },
            facingMode: 'user' // 전면 카메라 사용
          },
          audio: false
        });

        streamRef.current = stream;

        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          setScanStatus('얼굴을 인식하고 있습니다...');
        }
      } catch (error) {
        console.error('카메라 접근 오류:', error);
        setCameraError('카메라에 접근할 수 없습니다. 카메라 권한을 확인해주세요.');
        setScanStatus('카메라 접근 실패');
      }
    };

    startCamera();

    // 얼굴인식 시뮬레이션
    const scanningInterval = setInterval(() => {
      if (!cameraError) {
        const statuses = [
          '얼굴을 인식하고 있습니다...',
          '얼굴 특징을 분석 중...',
          '신원을 확인하고 있습니다...',
          '얼굴인식 진행 중...'
        ];
        const randomStatus = statuses[Math.floor(Math.random() * statuses.length)];
        setScanStatus(randomStatus);
      }
    }, 2000);

    // 정리 함수
    return () => {
      clearInterval(scanningInterval);
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
