import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Background from './Background';
import { useCart } from '../contexts/CartContext';
import { useToast } from '../hooks/useToast';
import ToastContainer from './ToastContainer';

const CartPage = () => {
  const { items: cartItems, removeFromCart, updateQuantity, getTotalPrice, getTotalItems } = useCart();
  const { toasts, showSuccess, showError, removeToast } = useToast();
  const [selectedItems, setSelectedItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('card');

  // cartItemsÍ∞Ä Î≥ÄÍ≤ΩÎê† Îïå selectedItems ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (cartItems && cartItems.length > 0) {
      setSelectedItems(cartItems.map(item => item.id));
    } else {
      setSelectedItems([]);
    }
  }, [cartItems]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('ko-KR').format(price);
  };

  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity >= 1) {
      updateQuantity(id, newQuantity);
    }
  };

  const handleRemoveItem = (id) => {
    const item = cartItems.find(item => item.id === id);
    removeFromCart(id);
    setSelectedItems(prev => prev.filter(itemId => itemId !== id));
    if (item) {
      showSuccess(`${item.name}Ïù¥(Í∞Ä) Ïû•Î∞îÍµ¨ÎãàÏóêÏÑú Ï†úÍ±∞ÎêòÏóàÏäµÎãàÎã§`, 2500);
    }
  };

  const handleItemSelect = (id) => {
    setSelectedItems(prev => 
      prev.includes(id) 
        ? prev.filter(itemId => itemId !== id)
        : [...prev, id]
    );
  };

  const handleSelectAll = () => {
    if (selectedItems.length === (cartItems || []).length) {
      setSelectedItems([]);
    } else {
      setSelectedItems((cartItems || []).map(item => item.id));
    }
  };

  const getSelectedTotal = () => {
    return (cartItems || [])
      .filter(item => selectedItems.includes(item.id))
      .reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const selectedTotal = getSelectedTotal();
  const shippingFee = selectedTotal >= 50000 ? 0 : 3000;
  const discount = 10000; // ÏòàÏãú Ìï†Ïù∏
  const finalPrice = selectedTotal + shippingFee - discount;

  return (
    <div className="App">
      <Background />
      <ToastContainer toasts={toasts} removeToast={removeToast} />

      <main className="container">
        {/* Header Section */}
        <section className="card hero">
          <div className="hero-content">
            <div className="brand">
              <div className="kakao-logo">
