import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import Background from './Background';
import { productAPI } from '../utils/api';
import { useCart } from '../contexts/CartContext';

const CakeDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, getTotalItems } = useCart();
  const [cake, setCake] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('1호 (4-6인분)');
  const [selectedFlavor, setSelectedFlavor] = useState('딸기');
  const [activeTab, setActiveTab] = useState('상품 정보');

  // 예시 케이크 데이터
  const sampleCakes = {
    1: {
      id: 1,
      name: '딸기 생크림 케이크',
      description: '신선한 딸기와 부드러운 생크림의 완벽한 조화를 이룬 프리미엄 케이크입니다. 매일 아침 수확한 딸기와 정성스럽게 휘핑한 생크림으로 만들어져 더욱 신선하고 맛있습니다.',
      price: 35000,
      originalPrice: 42000,
      rating: 4.9,
      reviewCount: 127,
      images: [
        'https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1563729784474-d77dbb933a9e?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1571115764595-644a1f56a55c?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=600&h=400&fit=crop&crop=center'
      ],
      badge: 'BEST',
      category: '생일케이크',
      ingredients: ['딸기', '생크림', '밀가루', '설탕', '계란', '버터'],
      allergens: ['밀', '계란', '우유'],
      shelfLife: '냉장 보관, 3일 이내 섭취 권장',
      nutrition: {
        calories: 320,
        protein: 6,
        fat: 12,
        carbs: 45
      }
    },
    2: {
      id: 2,
      name: '초콜릿 무스 케이크',
      description: '진한 초콜릿과 부드러운 무스의 완벽한 조화를 이룬 프리미엄 케이크입니다. 벨기에산 고급 초콜릿을 사용하여 깊고 진한 맛을 선사합니다.',
      price: 45000,
      originalPrice: null,
      rating: 4.7,
      reviewCount: 89,
      images: [
        'https://images.unsplash.com/photo-1606312619070-d48b4c652a52?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1563729784474-d77dbb933a9e?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1571115764595-644a1f56a55c?w=600&h=400&fit=crop&crop=center'
      ],
      badge: 'NEW',
      category: '생일케이크',
      ingredients: ['초콜릿', '생크림', '밀가루', '설탕', '계란', '버터'],
      allergens: ['밀', '계란', '우유'],
      shelfLife: '냉장 보관, 3일 이내 섭취 권장',
      nutrition: {
        calories: 380,
        protein: 7,
        fat: 18,
        carbs: 42
      }
    },
    3: {
      id: 3,
      name: '뉴욕 치즈케이크',
      description: '진짜 뉴욕의 맛, 진한 치즈의 프리미엄 치즈케이크입니다. 크림치즈의 진한 맛과 부드러운 질감이 일품입니다.',
      price: 28000,
      originalPrice: null,
      rating: 4.3,
      reviewCount: 156,
      images: [
        'https://images.unsplash.com/photo-1565958011703-44f9829ba187?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1563729784474-d77dbb933a9e?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1571115764595-644a1f56a55c?w=600&h=400&fit=crop&crop=center'
      ],
      badge: null,
      category: '디저트',
      ingredients: ['크림치즈', '생크림', '밀가루', '설탕', '계란', '버터'],
      allergens: ['밀', '계란', '우유'],
      shelfLife: '냉장 보관, 3일 이내 섭취 권장',
      nutrition: {
        calories: 350,
        protein: 8,
        fat: 22,
        carbs: 28
      }
    },
    4: {
      id: 4,
      name: '이탈리안 티라미수',
      description: '정통 이탈리안 레시피로 만든 프리미엄 티라미수입니다. 마스카포네 치즈와 에스프레소의 완벽한 조화를 이룹니다.',
      price: 38000,
      originalPrice: null,
      rating: 4.9,
      reviewCount: 203,
      images: [
        'https://images.unsplash.com/photo-1571877227200-a0d98ea607e9?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1563729784474-d77dbb933a9e?w=600&h=400&fit=crop&crop=center',
        'https://images.unsplash.com/photo-1571115764595-644a1f56a55c?w=600&h=400&fit=crop&crop=center'
      ],
      badge: '시즌한정',
      category: '디저트',
      ingredients: ['마스카포네', '에스프레소', '마스카포네', '설탕', '계란', '코코아'],
      allergens: ['계란', '우유', '카페인'],
      shelfLife: '냉장 보관, 2일 이내 섭취 권장',
      nutrition: {
        calories: 290,
        protein: 6,
        fat: 16,
        carbs: 32
      }
    }
  };

  useEffect(() => {
    const fetchCake = async () => {
      setLoading(true);
      try {
        // 백엔드에서 상품 상세 정보 가져오기
        const data = await productAPI.getProduct(id);
        setCake(data);
      } catch (error) {
        console.error('상품 상세 조회 실패:', error);
        // 에러 시 예시 데이터 사용
        const sampleCake = sampleCakes[id];
        if (sampleCake) {
          setCake(sampleCake);
        } else {
          console.error('상품을 찾을 수 없습니다:', id);
          navigate('/cakes');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchCake();
  }, [id, navigate]);

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1 && newQuantity <= 10) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    if (cake) {
      const cartItem = {
        ...cake,
        image: cake.images[selectedImage],
        quantity: quantity,
        selectedSize: selectedSize,
        selectedFlavor: selectedFlavor
      };
      addToCart(cartItem);
      alert(`${cake.name} ${quantity}개가 장바구니에 추가되었습니다!`);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('ko-KR').format(price);
  };

  const getDiscountPercentage = () => {
    if (cake && cake.originalPrice && cake.originalPrice > cake.price) {
      return Math.round((1 - cake.price / cake.originalPrice) * 100);
    }
    return 0;
  };

  if (loading) {
    return (
      <div className="App">
        <Background />
        <div className="relative z-10 min-h-screen flex items-center justify-center">
          <div className="text-white text-xl">로딩 중...</div>
        </div>
      </div>
    );
  }

  if (!cake) {
    return (
      <div className="App">
        <Background />
        <div className="relative z-10 min-h-screen flex items-center justify-center">
          <div className="text-white text-xl">상품을 찾을 수 없습니다.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <Background />

      {/* 장바구니 버튼 (고정 위치) */}
      <div className="fixed top-4 right-4 z-50">
        <Link
          to="/cart"
          className="relative bg-pink-600 hover:bg-pink-700 text-white p-3 rounded-full shadow-lg transition-all duration-300 hover:scale-110 group"
          title="장바구니"
        >
          <span className="text-xl">🛒</span>
          {getTotalItems() > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center font-bold animate-pulse">
              {getTotalItems()}
            </span>
          )}
          <div className="absolute right-full top-1/2 transform -translate-y-1/2 mr-2 bg-gray-800 text-white px-2 py-1 rounded text-sm opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
            장바구니 ({getTotalItems()}개)
          </div>
        </Link>
      </div>

      <main className="container">
        {/* Breadcrumb Navigation */}
        <nav className="breadcrumb">
          <Link to="/cakes" className="breadcrumb-link">케이크 컬렉션</Link>
          <span className="breadcrumb-separator">></span>
          <span className="breadcrumb-current">{cake.name}</span>
        </nav>

        {/* Product Detail Section */}
        <section className="product-detail">
          <div className="product-gallery">
            <div className="main-image">
              <img src={cake.images[selectedImage]} alt={cake.name} />
              {cake.badge && (
                <div className="product-badge">{cake.badge}</div>
              )}
              <button className="btn-icon gallery-zoom">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
                  <path d="M19 19l-4.35-4.35M17 9A8 8 0 1 1 1 9a8 8 0 0 1 16 0z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>
            </div>
            <div className="thumbnail-gallery">
              {cake.images.map((image, index) => (
                <div 
                  key={index}
                  className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
                  onClick={() => setSelectedImage(index)}
                >
                  <img src={image} alt={`${cake.name} ${index + 1}`} />
                </div>
              ))}
            </div>
          </div>

          <div className="product-info">
            <div className="product-header">
              <h1 className="product-title">{cake.name}</h1>
              <div className="product-rating">
                <div className="stars">★★★★★</div>
                <span className="rating-text">{cake.rating} ({cake.reviewCount}개 리뷰)</span>
                <a href="#reviews" className="review-link">리뷰 보기</a>
              </div>
            </div>

            <div className="product-description">
              <p>{cake.description}</p>
            </div>

            <div className="product-specs">
              <div className="spec-item">
                <span className="spec-label">크기</span>
                <div className="spec-options">
                  <button 
                    className={`spec-option ${selectedSize === '1호 (4-6인분)' ? 'active' : ''}`}
                    onClick={() => setSelectedSize('1호 (4-6인분)')}
                  >
                    1호 (4-6인분)
                  </button>
                  <button 
                    className={`spec-option ${selectedSize === '2호 (8-10인분)' ? 'active' : ''}`}
                    onClick={() => setSelectedSize('2호 (8-10인분)')}
                  >
                    2호 (8-10인분)
                  </button>
                  <button 
                    className={`spec-option ${selectedSize === '3호 (12-15인분)' ? 'active' : ''}`}
                    onClick={() => setSelectedSize('3호 (12-15인분)')}
                  >
                    3호 (12-15인분)
                  </button>
                </div>
              </div>
              <div className="spec-item">
                <span className="spec-label">맛</span>
                <div className="spec-options">
                  <button 
                    className={`spec-option ${selectedFlavor === '딸기' ? 'active' : ''}`}
                    onClick={() => setSelectedFlavor('딸기')}
                  >
                    딸기
                  </button>
                  <button 
                    className={`spec-option ${selectedFlavor === '바닐라' ? 'active' : ''}`}
                    onClick={() => setSelectedFlavor('바닐라')}
                  >
                    바닐라
                  </button>
                  <button 
                    className={`spec-option ${selectedFlavor === '초콜릿' ? 'active' : ''}`}
                    onClick={() => setSelectedFlavor('초콜릿')}
                  >
                    초콜릿
                  </button>
                </div>
              </div>
            </div>

            <div className="product-price-section">
              <div className="price-info">
                <span className="current-price">₩{formatPrice(cake.price)}</span>
                {cake.originalPrice && (
                  <>
                    <span className="original-price">₩{formatPrice(cake.originalPrice)}</span>
                    <span className="discount-badge">{getDiscountPercentage()}% 할인</span>
                  </>
                )}
              </div>
              <div className="delivery-info">
                <div className="delivery-item">
                  <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                    <path d="M8 0L10.5 5.5H16L11.5 9L13 14.5L8 11L3 14.5L4.5 9L0 5.5H5.5L8 0Z" fill="currentColor"/>
                  </svg>
                  <span>무료배송</span>
                </div>
                <div className="delivery-item">
                  <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                    <path d="M8 0C3.589 0 0 3.589 0 8s3.589 8 8 8 8-3.589 8-8-3.589-8-8-8zm0 14c-3.314 0-6-2.686-6-6s2.686-6 6-6 6 2.686 6 6-2.686 6-6 6z" fill="currentColor"/>
                  </svg>
                  <span>당일 제작</span>
                </div>
              </div>
            </div>

            <div className="product-actions">
              <div className="quantity-selector">
                <button 
                  className="quantity-btn" 
                  onClick={() => handleQuantityChange(quantity - 1)}
                >
                  -
                </button>
                <span className="quantity">{quantity}</span>
                <button 
                  className="quantity-btn" 
                  onClick={() => handleQuantityChange(quantity + 1)}
                >
                  +
                </button>
              </div>
              <button 
                className="btn kakao-primary add-to-cart-large"
                onClick={handleAddToCart}
              >
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
                  <path d="M7 4V2C7 1.45 7.45 1 8 1H12C12.55 1 13 1.45 13 2V4H16C16.55 4 17 4.45 17 5S16.55 6 16 6H15V15C15 16.1 14.1 17 13 17H7C5.9 17 5 16.1 5 15V6H4C3.45 6 3 5.55 3 5S3.45 4 4 4H7ZM9 3V4H11V3H9ZM6 6V15C6 15.55 6.45 16 7 16H13C13.55 16 14 15.55 14 15V6H6Z" fill="currentColor"/>
                </svg>
                장바구니 담기
              </button>
              <button className="btn secondary buy-now">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
                  <path d="M10 0C4.477 0 0 4.477 0 10s4.477 10 10 10 10-4.477 10-10S15.523 0 10 0zm0 18c-4.411 0-8-3.589-8-8s3.589-8 8-8 8 3.589 8 8-3.589 8-8 8z" fill="currentColor"/>
                  <path d="M10 4c-3.314 0-6 2.686-6 6s2.686 6 6 6 6-2.686 6-6-2.686-6-6-6zm0 10c-2.206 0-4-1.794-4-4s1.794-4 4-4 4 1.794 4 4-1.794 4-4 4z" fill="currentColor"/>
                </svg>
                바로 구매
              </button>
            </div>

            <div className="product-features">
              <div className="feature-item">
                <div className="feature-icon">🍓</div>
                <div className="feature-text">
                  <h4>신선한 재료</h4>
                  <p>매일 아침 수확한 신선한 재료 사용</p>
                </div>
              </div>
              <div className="feature-item">
                <div className="feature-icon">🥛</div>
                <div className="feature-text">
                  <h4>프리미엄 크림</h4>
                  <p>정성스럽게 휘핑한 고급 크림</p>
                </div>
              </div>
              <div className="feature-item">
                <div className="feature-icon">🏠</div>
                <div className="feature-text">
                  <h4>수제 제작</h4>
                  <p>경험 많은 셰프의 손으로 정성스럽게 제작</p>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Product Details Tabs */}
        <section className="product-tabs">
          <div className="tab-navigation">
            <button 
              className={`tab-btn ${activeTab === '상품 정보' ? 'active' : ''}`}
              onClick={() => setActiveTab('상품 정보')}
            >
              상품 정보
            </button>
            <button 
              className={`tab-btn ${activeTab === '리뷰' ? 'active' : ''}`}
              onClick={() => setActiveTab('리뷰')}
            >
              리뷰 ({cake.reviewCount})
            </button>
            <button 
              className={`tab-btn ${activeTab === '배송/교환' ? 'active' : ''}`}
              onClick={() => setActiveTab('배송/교환')}
            >
              배송/교환
            </button>
            <button 
              className={`tab-btn ${activeTab === '문의하기' ? 'active' : ''}`}
              onClick={() => setActiveTab('문의하기')}
            >
              문의하기
            </button>
          </div>

          <div className="tab-content">
            {activeTab === '상품 정보' && (
              <div className="tab-panel active">
                <h3>상품 상세 정보</h3>
                <div className="detail-grid">
                  <div className="detail-item">
                    <span className="detail-label">원재료</span>
                    <span className="detail-value">{cake.ingredients.join(', ')}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">알레르기 정보</span>
                    <span className="detail-value">{cake.allergens.join(', ')} 함유</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">보관 방법</span>
                    <span className="detail-value">{cake.shelfLife}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">제조일</span>
                    <span className="detail-value">주문일 기준 당일 제작</span>
                  </div>
                </div>

                <div className="nutrition-info">
                  <h4>영양 정보 (100g 기준)</h4>
                  <div className="nutrition-grid">
                    <div className="nutrition-item">
                      <span>칼로리</span>
                      <span>{cake.nutrition.calories}kcal</span>
                    </div>
                    <div className="nutrition-item">
                      <span>탄수화물</span>
                      <span>{cake.nutrition.carbs}g</span>
                    </div>
                    <div className="nutrition-item">
                      <span>단백질</span>
                      <span>{cake.nutrition.protein}g</span>
                    </div>
                    <div className="nutrition-item">
                      <span>지방</span>
                      <span>{cake.nutrition.fat}g</span>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === '리뷰' && (
              <div className="tab-panel">
                <h3>고객 리뷰</h3>
                <p>리뷰 기능은 준비 중입니다.</p>
              </div>
            )}

            {activeTab === '배송/교환' && (
              <div className="tab-panel">
                <h3>배송/교환 안내</h3>
                <p>배송/교환 정보는 준비 중입니다.</p>
              </div>
            )}

            {activeTab === '문의하기' && (
              <div className="tab-panel">
                <h3>상품 문의</h3>
                <p>문의 기능은 준비 중입니다.</p>
              </div>
            )}
          </div>
        </section>

        {/* Related Products */}
        <section className="related-products">
          <h2>관련 상품</h2>
          <div className="related-grid">
            {Object.values(sampleCakes)
              .filter(item => item.id !== cake.id)
              .slice(0, 3)
              .map((relatedCake) => (
                <div key={relatedCake.id} className="related-card">
                  <img src={relatedCake.images[0]} alt={relatedCake.name} />
                  <h4>{relatedCake.name}</h4>
                  <span className="price">₩{formatPrice(relatedCake.price)}</span>
                </div>
              ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default CakeDetailPage;
