import React, { createContext, useContext, useReducer, useEffect } from 'react';

// 장바구니 상태 관리
const CartContext = createContext();

// 장바구니 액션 타입
const CART_ACTIONS = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM',
  UPDATE_QUANTITY: 'UPDATE_QUANTITY',
  CLEAR_CART: 'CLEAR_CART',
  LOAD_CART: 'LOAD_CART'
};

// 장바구니 리듀서
const cartReducer = (state, action) => {
  switch (action.type) {
    case CART_ACTIONS.ADD_ITEM:
      const existingItem = state.items.find(item => item.id === action.payload.id);
      if (existingItem) {
        return {
          ...state,
          items: state.items.map(item =>
            item.id === action.payload.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        };
      } else {
        return {
          ...state,
          items: [...state.items, { ...action.payload, quantity: 1 }]
        };
      }

    case CART_ACTIONS.REMOVE_ITEM:
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.payload)
      };

    case CART_ACTIONS.UPDATE_QUANTITY:
      return {
        ...state,
        items: state.items.map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: Math.max(0, action.payload.quantity) }
            : item
        ).filter(item => item.quantity > 0)
      };

    case CART_ACTIONS.CLEAR_CART:
      return {
        ...state,
        items: []
      };

    case CART_ACTIONS.LOAD_CART:
      return {
        ...state,
        items: action.payload || []
      };

    default:
      return state;
  }
};

// 예시 데이터
const sampleCartItems = [
  {
    id: 1,
    name: '딸기 생크림 케이크',
    category: '생일케이크',
    price: 35000,
    originalPrice: 42000,
    image: 'https://i.pinimg.com/1200x/ad/62/12/ad6212628fa7ca2851db2f90bef2bf58.jpg',
    quantity: 2
  },
  {
    id: 2,
    name: '초콜릿 무스 케이크',
    category: '생일케이크',
    price: 38000,
    originalPrice: 45000,
    image: 'https://i.pinimg.com/1200x/8c/92/84/8c9284e8c4e5b5e5e5e5e5e5e5e5e5e5.jpg',
    quantity: 1
  },
  {
    id: 3,
    name: '티라미수',
    category: '디저트',
    price: 28000,
    originalPrice: null,
    image: 'https://i.pinimg.com/1200x/7b/3a/45/7b3a45e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg',
    quantity: 3
  },
  {
    id: 4,
    name: '웨딩 케이크',
    category: '웨딩케이크',
    price: 120000,
    originalPrice: 150000,
    image: 'https://i.pinimg.com/1200x/9c/4a/67/9c4a67e5e5e5e5e5e5e5e5e5e5e5e5e5.jpg',
    quantity: 1
  }
];

// 초기 상태
const initialState = {
  items: sampleCartItems // 예시 데이터로 초기화
};

// 장바구니 Provider
export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, initialState);

  // 로컬 스토리지에서 장바구니 로드
  useEffect(() => {
    const savedCart = localStorage.getItem('khanyisa_cart');
    if (savedCart) {
      try {
        const cartData = JSON.parse(savedCart);
        dispatch({ type: CART_ACTIONS.LOAD_CART, payload: cartData });
      } catch (error) {
        console.error('장바구니 로드 실패:', error);
        // 에러 시 예시 데이터 사용
        dispatch({ type: CART_ACTIONS.LOAD_CART, payload: sampleCartItems });
      }
    } else {
      // 저장된 데이터가 없으면 예시 데이터 사용
      dispatch({ type: CART_ACTIONS.LOAD_CART, payload: sampleCartItems });
    }
  }, []);

  // 장바구니 변경 시 로컬 스토리지에 저장
  useEffect(() => {
    localStorage.setItem('khanyisa_cart', JSON.stringify(state.items));
  }, [state.items]);

  // 장바구니에 상품 추가
  const addToCart = (product) => {
    dispatch({ type: CART_ACTIONS.ADD_ITEM, payload: product });
  };

  // 장바구니에서 상품 제거
  const removeFromCart = (productId) => {
    dispatch({ type: CART_ACTIONS.REMOVE_ITEM, payload: productId });
  };

  // 상품 수량 업데이트
  const updateQuantity = (productId, quantity) => {
    dispatch({ type: CART_ACTIONS.UPDATE_QUANTITY, payload: { id: productId, quantity } });
  };

  // 장바구니 비우기
  const clearCart = () => {
    dispatch({ type: CART_ACTIONS.CLEAR_CART });
  };

  // 장바구니 총 개수
  const getTotalItems = () => {
    return state.items.reduce((total, item) => total + item.quantity, 0);
  };

  // 장바구니 총 가격
  const getTotalPrice = () => {
    return state.items.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // 할인 적용된 총 가격
  const getDiscountedTotal = () => {
    return state.items.reduce((total, item) => {
      const originalPrice = item.originalPrice || item.price;
      return total + (originalPrice * item.quantity);
    }, 0);
  };

  // 총 할인 금액
  const getTotalDiscount = () => {
    return getDiscountedTotal() - getTotalPrice();
  };

  const value = {
    items: state.items,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotalItems,
    getTotalPrice,
    getDiscountedTotal,
    getTotalDiscount
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};

// 장바구니 Hook
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export default CartContext;
